                <p>
                    <label>Option 1:  </label>
                    <input type="text" className="form-control col-md-5"onChange={(e)=>setOption1({...Option1,option:e.target.value,vote:0})}/>
                </p>
                <p>
                    <label>Option 2:  </label>
                    <input type="text" className="form-control col-md-5" onChange={(e)=>setOption2({...Option2,option:e.target.value,vote:0})}/>
                </p>
                <p>
                    <label>Option 3:  </label>
                    <input type="text" className="form-control col-md-5" onChange={(e)=>setOption3({...Option4,option:e.target.value,vote:0})}/>
