import React, { useEffect,useState } from 'react';
import {Link} from '@reach/router';
import Axios from 'axios';
const DetailPet = (props) =>{
    const [PetName, setPetName] = useState('');
    const [PetType, setPetType] = useState("");
    const [PetDesc, setPetDesc] = useState("");
    const [Skill1, setSkill1] = useState("");
    const [Skill2, setSkill2] = useState("");
    const [Skill3, setSkill3] = useState("");
    useEffect(()=>{
        Axios.get("http://localhost:8000/details/"+ props.id)
        .then(res=>{
            setPetName(res.data.PetName)
            setPetType(res.data.PetType)
            setPetDesc(res.data.PetDesc)
            setSkill1(res.data.Skill1)
            setSkill2(res.data.Skill2)
            setSkill3(res.data.Skill3)
        })

    },[])
    return (
        <div className="conatiner col-lg-10">
            <h1>Pet Shelter</h1>
            <h1>Details about : {PetName}</h1>
            <Link to="/"><button className="btn btn-primary">Back to Home</button></Link>

            <Link to={"/delpet/"+ props.id}><button className="btn btn-danger">Adapt {PetName}</button></Link>
            <p>
                <h4> Pet Type : </h4>
                <span>{PetType}</span>
            </p>
            <p>
                <h4> Pet Description : </h4>
                <span>{PetType}</span>
            </p>
            <p>
                <h4> Skills : </h4>
                    <ul>
                        <li>{Skill1}</li>
                        <li>{Skill2}</li>
                        <li>{Skill3}</li>
                    </ul>
            </p>
        </div>
    )
}
export default DetailPet;
