import React, { useEffect,useState } from 'react';
import {Link} from '@reach/router';
import Axios from 'axios';
const DetailPet = (props) =>{
    const [PetName, setPetName] = useState('');
    // const [PetType, setPetType] = useState("");
    // const [PetDesc, setPetDesc] = useState("");
    // const [Skill1, setSkill1] = useState("");
    // const [Skill2, setSkill2] = useState("");
    // const [Skill3, setSkill3] = useState("");
    console.log(props.id)
    useEffect(()=>{
        Axios.get("http://localhost:8000/details/"+ props.id)
        .then(res=>{
            setPetName(res.data.PetName)
            console.log(res.data.PetName)
        })

    })
    return (
        <div className="conatiner col-lg-10">
            <h1>Pet Shelter</h1>
            <h1>Details about : Pet Name</h1>
            <Link to="/delpet/:id"><button>Adapt Pet Name</button></Link>
        </div>
    )
}
export default DetailPet;
