import React, { useState ,useEffect} from 'react';
import { Link } from '@reach/router';
import axios from'axios';

const AllPets = ()=>{
    const [allPets,setAllPets] = useState({})
    const fetchAllPets=()=>{
        axios.get("http://localhost:8000/allPets")
        .then(res=>{setAllPets(res.data)
            console.log(res.data)
        })
    }
    useEffect(()=>{
        fetchAllPets();
        },[allPets])

    return(

        <div className="container col-lg-8">
            <navbar>
            <button className="btn btn-info"> Add a Pet to the Shelter</button>
            </navbar>
            <h1>Pet Shelter</h1>
            <h3>These Pet are Looking for good home</h3>
            <table className="table table-striped table-bordered">
                <thead className="thead thead-dark">
                    <th>Name</th>
                    <th>Type</th>
                    <th>Action</th>
                </thead>
                <tbody>
                    { allPets ? allPets.map(pet=>(
                    <tr>
                    <td></td>
                    <td></td>
                    <td>
                        <Link to="/details/:id"><button className="btn btn-warning">Details</button></Link>
                        <Link to="/update/:id"><button className="btn btn-primary">Edit</button></Link>
                    </td>
                    </tr>
                    )):null}

                </tbody>
            </table>
        </div>
    )
}
export default AllPets;
